definition.script:
// Name the build out of the parameters
import groovy.json.JsonBuilder
import groovy.json.JsonSlurperClassic
import org.jenkinsci.plugins.credentialsbinding.impl.CredentialNotFoundException

def jenkinsCredentials = ""

node {
 def skipRemainingStages = true
 def buildName = "${instanceUuid}"
 def cause = ""
 def sep = " + "
 if (artifactsChanges == "true") { cause += "artifacts" + sep }
 if (dataChanges == "true") { cause += "data" + sep }
 if (deploymentChanges == "true") { cause += "deployment" + sep }
 if (propertiesChanges == "true") { cause += "properties" + sep }
 if (creation == "true") { cause += "creation" + sep }
 cause = cause.substring(0, cause.length() - sep.length())
 buildName += ": " + cause
 currentBuild.displayName = buildName
 currentBuild.description = buildName
}

pipeline {
 agent any
 options {
   ansiColor('xterm')
 }
 tools {
  maven 'Maven 3.5.0'
  nodejs 'NodeJS 10.11.0'
 }
 stages {
  stage('Load Jenkins Credentials') {
   steps {
    script {
     // Fetch the instance definition to retrieve 'group' and 'type' values
     sh 'node /opt/node-scripts/src/$JOB_NAME/fetch-instance-def.js'
     def instanceDefJSON = sh (
      script: '$BUILD_PATH/fetch-instance-def.sh',
      returnStdout: true
     )
     def instanceDef = new JsonSlurperClassic().parseText(instanceDefJSON)

     // Fetch Jenkins Credentials for the Group and Type
     def filter = ["${instanceDef.group}", "${instanceDef.group}_${instanceDef.type}"]
     def credentials = []
     for (key in filter) {
      println "\033[1m\033[34m[INFO]\033[0m Fetching '${key}' Jenkins Credential."
      try {
       withCredentials([string(credentialsId: key, variable: 'SECRET')]) {
        credentials.push(new JsonSlurperClassic().parseText(env.SECRET))
        println "\033[1m\033[34m[INFO]\033[0m Sucessfully fetched '${key}' Jenkins Credential."
       }
      } catch (CredentialNotFoundException e) {
       println "\033[1m\033[33m[WARN]\033[0m '${key}' Jenkins Credential not found. Skipping."
       currentBuild.result = 'SUCCESS'
      }
     }
     if (credentials.size() == 0) {
      println "\033[1m\033[33m[WARN]\033[0m No Jenkins Credentials found. Won't be able to process secrets (if any) in the instance definition."
     }

     // Save the Jenkins Credentials as JSON
     jenkinsCredentials = new JsonBuilder(credentials).toPrettyString()
    }
   }
  }
  stage('Pre-host connection preparation') {
   environment {
    SECRETS = "${jenkinsCredentials}"
   }

   steps {
    sh 'node /opt/node-scripts/src/$JOB_NAME/prehost-preparation.js'
    sh 'cat $BUILD_PATH/prehost-prepare.sh'
    sh '$BUILD_PATH/prehost-prepare.sh'
    sh 'node /opt/node-scripts/src/$JOB_NAME/update-status.js $BUILD_PATH/status.json'
   }
  }
  stage('Instance status') {
    steps{
      script {
      sh 'node /opt/node-scripts/src/$JOB_NAME/fetch-instance-def.js'
      def instanceDefJSON = sh (
      script: '$BUILD_PATH/fetch-instance-def.sh',
      returnStdout: true
      )
     def instanceDef = new JsonSlurperClassic().parseText(instanceDefJSON)
     skipRemainingStages = instanceDef.active
      if (!instanceDef.active) {
      currentBuild.build().getExecutor().interrupt(Result.SUCCESS)
      currentBuild.result = 'SUCCESS'
      return
        }
      }
    }
  }
  stage('Host preparation') {
   environment {
    SECRETS = "${jenkinsCredentials}"
   }
   when {
      expression {
        skipRemainingStages
      }
    }
   steps {
    sh 'node /opt/node-scripts/src/$JOB_NAME/host-preparation.js'
    sh 'cat $BUILD_PATH/host-prepare.sh'
    sh '$BUILD_PATH/host-prepare.sh'
    sh 'node /opt/node-scripts/src/$JOB_NAME/update-status.js $BUILD_PATH/status.json'
   }
  }
  stage('Maintenance mode: ON') {
   environment {
    SECRETS = "${jenkinsCredentials}"
   }
   steps {
    sh 'node /opt/node-scripts/src/$JOB_NAME/maintenance-on.js'
    sh 'cat $BUILD_PATH/set-maintenance-on.sh'
    sh '$BUILD_PATH/set-maintenance-on.sh'
    sh 'node /opt/node-scripts/src/$JOB_NAME/update-status.js $BUILD_PATH/status.json'
   }
  }
  stage('Production host preparation') {
   environment {
    SECRETS = "${jenkinsCredentials}"
   }
   when {
    expression {
     return false
    }
    expression {
      skipRemainingStages
    }
   }
   steps {
    sh 'echo "Stage/steps awaiting implementation... Skipping."'
   }
  }
  stage('Start or restart the instance') {
   environment {
    SECRETS = "${jenkinsCredentials}"
   }
   when {
      expression {
        skipRemainingStages
      }
    }
   steps {
    sh 'node /opt/node-scripts/src/$JOB_NAME/start-instance.js'
    sh 'cat $BUILD_PATH/start-instance.sh'
    sh '$BUILD_PATH/start-instance.sh'
    sh 'node /opt/node-scripts/src/$JOB_NAME/update-status.js $BUILD_PATH/status.json'
   }
  }
  stage('Instance startup status monitoring') {
   environment {
    SECRETS = "${jenkinsCredentials}"
   }
   when {
      expression {
        skipRemainingStages
      }
    }
   steps {
    sh 'node /opt/node-scripts/src/$JOB_NAME/startup-monitoring.js'
    sh 'cat $BUILD_PATH/startup-monitoring.sh'
    sh '$BUILD_PATH/startup-monitoring.sh'
    sh 'node /opt/node-scripts/src/$JOB_NAME/update-status.js $BUILD_PATH/status.json'
   }
  }
  stage('Post startup actions') {
   environment {
    SECRETS = "${jenkinsCredentials}"
   }
   when {
      expression {
        skipRemainingStages
      }
    }
   steps {
    sh 'node /opt/node-scripts/src/$JOB_NAME/post-start.js'
    sh 'cat $BUILD_PATH/post-start.sh'
    sh '$BUILD_PATH/post-start.sh'
    sh 'node /opt/node-scripts/src/$JOB_NAME/update-status.js $BUILD_PATH/status.json'
   }
  }
  stage('Maintenance mode: OFF') {
   environment {
    SECRETS = "${jenkinsCredentials}"
   }
   when {
      expression {
        skipRemainingStages
      }
    }
   steps {
    sh 'node /opt/node-scripts/src/$JOB_NAME/maintenance-off.js'
    sh 'cat $BUILD_PATH/set-maintenance-off.sh'
    sh '$BUILD_PATH/set-maintenance-off.sh'
    sh 'node /opt/node-scripts/src/$JOB_NAME/update-status.js $BUILD_PATH/status.json'
   }
  }
 }
}
